import React from 'react'
import './BootcampProduct.css'

const BootcampProduct = ({ title, description, location, rating, photo }) => {
    return (
        <div className="product_container">
            <div className="product_photo" >
                <img src={photo} alt='photo' className='product_img'/>
            </div>
            <div className="product_content">
                <p className={'product_title'}>{ title }</p>
                <div className={'product_location'}>{ location }</div>
                <p className={'product_description'}>{ description }</p>
            </div>

            <div className="rating_box">
                { rating }
            </div>

        </div>
    )
}

export default BootcampProduct