import React from 'react'
import './Button.css'

const Button = ({title, className = '', width = '450px'}) => {
    return (
        <div className={'button_container'} style={{maxWidth: width}}>
            <button
                type={"submit"}
                className={'button_component' + ' ' + className}
                >{title}</button>
        </div>
    )
}

export default Button