import React, { useEffect } from 'react'
import './Input.css'

const Input = ({type = 'text', title, onChangeInput, className = '', width, name = '', }) => {

    return (
            <input
                type={type}
                placeholder={title}
                onChange={onChangeInput}
                className={'input_component' + ' ' + className}
                style={{ width }}
                name={name}
            />
    )
}

export default Input