import { Switch, Route } from 'react-router-dom'
import Home from 'pages/Home/Home'
import Login from 'pages/Login/Login'
import Register from 'pages/Register/Register'
import Bootcamp from 'pages/Bootcamp/Bootcamp'
import ResetPassword from 'pages/ResetPassword/ResetPassword'
import UpdatePassword from 'pages/UpdatePassword/UpdatePassword'
import UpdateUserDetails from 'pages/UpdateUserDetails/UpdateUserDetails'


export default () => {
    return (
        <Switch>
            <Route path={'/'} component={Home} exact />
            <Route path={'/login'} component={Login} />
            <Route path={'/register'} component={Register} />
            <Route path={'/bootcamps'} component={Bootcamp} />
            <Route path={'/reset'} component={ResetPassword} />
            <Route path={'/update'} component={UpdatePassword} />
            <Route path={'/updateuser'} component={UpdateUserDetails} />
        </Switch>
    )
}